function generateUserAgent(deviceOption = '', versionOption = '') {
    // 用于随机生成UA
    // 输入的两个参数大小不敏感，

    // 定义常见的浏览器、操作系统和设备类型
    var browsers = [
        { name: "Chrome", versions: ["120.0.6099.", "119.0.6045.", "118.0.5993.", "117.0.5938."], highCompat: "80.0.3987." },
        { name: "Firefox", versions: ["120.0.", "119.0.", "118.0.", "117.0."], highCompat: "78.0." },
        { name: "Safari", versions: ["17.0.", "16.6.", "16.5.", "16.4."], highCompat: "13.1." },
        { name: "Edge", versions: ["120.0.2210.", "119.0.2151.", "118.0.2088.", "117.0.2045."], highCompat: "87.0.664." }
    ];
    var osPC = [
        "Windows NT 10.0; Win64; x64",
        "Macintosh; Intel Mac OS X 13_5",
        "Linux x86_64"
    ];
    var osAndroid = [
        "Android 14; Mobile",
        "Android 13; Mobile",
        "Android 12; Mobile",
        "Android 11; Mobile"
    ];
    var osIOS = [
        "iPhone; CPU iPhone OS 17_0 like Mac OS X",
        "iPhone; CPU iPhone OS 16_6 like Mac OS X",
        "iPhone; CPU iPhone OS 16_5 like Mac OS X",
        "iPhone; CPU iPhone OS 16_4 like Mac OS X"
    ];
    var osHarmonyOS = [
        "HarmonyOS 4.0; Mobile",
        "HarmonyOS 3.0; Mobile",
        "HarmonyOS 2.0; Mobile"
    ];
    var osHyperOS = [
        "HyperOS 1.0; Mobile"
    ];
    var random = function(arr) {
        return arr[Math.floor(Math.random() * arr.length)];
    };

    // 根据第一个参数生成操作系统和设备类型
    var os, device;
    switch ((deviceOption || '').toLowerCase()) {
        case '电脑':
            os = random(osPC); // 随机选择 PC 操作系统
            device = ''; // PC 版不需要设备标识
            break;
        case '安卓': // 安卓版
            os = random(osAndroid); // 随机选择安卓操作系统
            device = 'Mobile'; // 添加手机设备标识
            break;
        case '苹果': // 苹果版
            os = random(osIOS); // 随机选择 iOS 操作系统
            device = 'Mobile'; // 添加手机设备标识
            break;
        case '鸿蒙': // 鸿蒙版
            os = random(osHarmonyOS); // 随机选择鸿蒙操作系统
            device = 'Mobile'; // 添加手机设备标识
            break;
        case 'oppo': // OPPO 手机
            os = "Android " + random(["15", "14", "13", "12", "11"]) + "; Mobile"; // 随机选择安卓版本
            device = 'OPPO; ' + random(["CPH2451", "CPH2415", "CPH2605", "CPH2305"]) + ' Build/' + random(["TP1A.220624.014", "TP1A.230720.002", "TP1A.231005.001", "TP1A.210720.011"]); // 随机选择设备型号和构建版本
            break;
        case 'vivo': // Vivo 手机
            os = "Android " + random(["15", "14", "13", "12", "11"]) + "; Mobile"; // 随机选择安卓版本
            device = 'Vivo; ' + random(["V2243A", "V2309A", "V2339A", "V2166A"]) + ' Build/' + random(["SKQ1.220303.001", "SKQ1.230723.002", "SKQ1.231101.001", "SKQ1.210720.012"]); // 随机选择设备型号和构建版本
            break;
        case '小米': // 小米手机
            var system = random(["MIUI", "HyperOS"]); // 随机选择 MIUI 或 HyperOS
            if (system === "MIUI") {
                os = "Android " + random(["15", "14", "13", "12", "11"]) + "; Mobile"; // MIUI 基于安卓
                device = 'Xiaomi; ' + random(["2210132C", "23127PN0CC", "2304FPN6DC", "M2103K19C"]) + ' Build/' + random(["TKQ1.221114.001", "TKQ1.230901.001", "TKQ1.231101.001", "TKQ1.210720.011"]); // MIUI 设备标识
            } else {
                os = random(osHyperOS); // HyperOS
                device = 'Xiaomi; ' + random(["23113RKC6C", "23117RK66C", "23127PN0CG", "Xiaomi 14"]) + ' Build/' + random(["HyperOS 1.0.001", "HyperOS 1.0.002", "HyperOS 1.0.003", "HyperOS 1.0.004"]); // HyperOS 设备标识
            }
            break;
        case '华为': // 华为手机
            os = random(osHarmonyOS); // 华为手机可能使用鸿蒙系统
            device = 'Huawei; ' + random(["NOH-AN00", "LIO-AN00", "ANA-AN00", "DCO-AL00"]) + ' Build/' + random(["HarmonyOS 4.0.001", "HarmonyOS 3.0.002", "HarmonyOS 2.0.003", "HarmonyOS 1.0.004"]); // 随机选择设备型号和构建版本
            break;
        case '三星': // 三星手机
            os = "Android " + random(["15", "14", "13", "12", "11"]) + "; Mobile"; // 随机选择安卓版本
            device = 'Samsung; ' + random(["SM-S9180", "SM-S9110", "SM-S9060", "SM-G9980"]) + ' Build/' + random(["SP1A.210812.016", "SP1A.230720.002", "SP1A.231101.001", "SP1A.210720.013"]); // 随机选择设备型号和构建版本
            break;
        case '一加': // 一加手机
            os = "Android " + random(["15", "14", "13", "12", "11"]) + "; Mobile"; // 随机选择安卓版本
            device = 'OnePlus; ' + random(["CPH2449", "CPH2451", "CPH2605", "IN2020"]) + ' Build/' + random(["OP1A.220624.014", "OP1A.230720.002", "OP1A.231005.001", "OP1A.210720.011"]); // 随机选择设备型号和构建版本
            break;
        case '魅族': // 魅族手机
            os = "Android " + random(["15", "14", "13", "12", "11"]) + "; Mobile"; // 随机选择安卓版本
            device = 'Meizu; ' + random(["MZ2110", "MZ2150", "MZ2170", "M1920"]) + ' Build/' + random(["MZ1A.220624.014", "MZ1A.230720.002", "MZ1A.231005.001", "MZ1A.210720.011"]); // 随机选择设备型号和构建版本
            break;
        case '努比亚': // 努比亚手机
            os = "Android " + random(["15", "14", "13", "12", "11"]) + "; Mobile"; // 随机选择安卓版本
            device = 'Nubia; ' + random(["NX712J", "NX713J", "NX715J", "NX651J"]) + ' Build/' + random(["NX1A.220624.014", "NX1A.230720.002", "NX1A.231005.001", "NX1A.210720.011"]); // 随机选择设备型号和构建版本
            break;
        case 'realme': // Realme 手机
            os = "Android " + random(["15", "14", "13", "12", "11"]) + "; Mobile"; // 随机选择安卓版本
            device = 'Realme; ' + random(["RMX3708", "RMX3710", "RMX3720", "RMX2200"]) + ' Build/' + random(["RM1A.220624.014", "RM1A.230720.002", "RM1A.231005.001", "RM1A.210720.011"]); // 随机选择设备型号和构建版本
            break;
        default: // 全随机
            var allOS = osPC.concat(osAndroid, osIOS, osHarmonyOS, osHyperOS); // 所有操作系统
            os = random(allOS); // 随机选择操作系统
            device = os.indexOf('Android') !== -1 || os.indexOf('iPhone') !== -1 || os.indexOf('HarmonyOS') !== -1 || os.indexOf('HyperOS') !== -1 ? 'Mobile' : ''; // 如果是手机/平板操作系统，添加设备标识
    }

    // 根据第二个参数生成版本号
    var browser = random(browsers);
    var version;
    switch ((versionOption || '').toLowerCase()) {
        case '高版本':
            version = random(browser.versions) + Math.floor(Math.random() * 100); // 高版本
            break;
        case '高兼容':
            version = browser.highCompat + Math.floor(Math.random() * 100); // 高兼容
            break;
        default: // 全随机
            version = (Math.floor(Math.random() * 100) + 1) + ".0." + Math.floor(Math.random() * 1000) + "." + Math.floor(Math.random() * 100); // 随机版本号
    }

    // 组合成完整的 UA 字符串
    return "Mozilla/5.0 (" + os + (device ? "; " + device : "") + ") AppleWebKit/537.36 (KHTML, like Gecko) " + browser.name + "/" + version + " Safari/537.36";
}

